import pyperclip
import keyboard
import requests
from bs4 import BeautifulSoup
import time
import pystray
from PIL import Image

def get\_url\_from\_clipboard():
 url = pyperclip.paste()
 if not url.startswith('http'):
 print('The text in the clipboard is not a URL')
 return None
 return url

def get\_simplest\_html(url):
 response = requests.get(url)
 if response.status\_code != 200:
 print(f'The URL {url} is not reachable')
 return None
 soup = BeautifulSoup(response.content, 'html.parser')
 return str(soup.html)

def setup\_keyboard\_shortcut(icon):
 keyboard.add\_hotkey('ctrl+shift+h', process\_url\_from\_clipboard)

def process\_url\_from\_clipboard():
 url = get\_url\_from\_clipboard()
 if url is None:
 return
 simplest\_html = get\_simplest\_html(url)
 if simplest\_html is None:
 return
 pyperclip.copy(simplest\_html)

def on\_quit(icon, item):
 icon.stop()

def start\_app():
 icon = pystray.Icon('name', Image.open('grabber.ico'), 'Simple HTML Scraper')
 icon.menu = pystray.Menu(
 pystray.MenuItem('Quit', on\_quit)
 )
 icon.run(setup\_keyboard\_shortcut)
 keyboard.wait()
